+template <typename Exception>
+struct ThrowEmplace
+{
+    static constexpr bool emplace = true;
+
+    template <typename... Params>
+    void operator()(Params&&... params)
+    {
+        throw Exception(std::forward<Params>(params)...);
+    }
+};
