+        invoke_context,
+        std::forward<Input>(input),
+        std::declval<Emplace<decltype(std::ref(value))>>()))* enable = nullptr)
+{
+    auto ref = std::ref(value);
+    ReadFieldNew(TypeList<Decay<LocalType>>(), invoke_context, input, Emplace<decltype(ref)>(ref));
