+
+//! Invoke void callable and save exception state in promise.
+template <typename Callable>
+void SetPromise(std::promise<void>& promise, Callable&& callable)
+{
+    try {
+        callable();
+        promise.set_value();
+    } catch (...) {
+        promise.set_exception(std::current_exception());
+    }
