+//!
+//!    [capture](auto arg) { do_something(arg); }
+//!
+//! which in c++11 has to be written like
+//!
+//!    struct DoSomething {
+//!        Capture m_capture;
+//!
+//!        template<typename Arg>
+//!        void operator()(Arg arg) {
+//!            return do_something(arg);
+//!        }
