+template <typename Interface, typename Impl>
+ProxyServerBase<Interface, Impl>::ProxyServerBase(Impl* impl, bool owned, EventLoop& loop, Connection* connection)
+    : m_impl(impl), m_owned(owned), m_connection(connection)
+{
+    assert(impl != nullptr);
+    AddClient(loop);
+}
