+
+template <typename Interface, typename Impl>
+ProxyServerBase<Interface, Impl>::ProxyServerBase(Impl* impl, bool owned, Connection& connection)
+    : ProxyServerBase(impl, owned, connection.m_loop, &connection)
+{
+}
+
+template <typename Interface, typename Impl>
+ProxyServerBase<Interface, Impl>::~ProxyServerBase()
+{
+    if (Impl* impl = m_impl) {
+        // If impl is non-null, it means client was not destroyed
+        // cleanly (was killed or disconnected). Since client isn't
+        // providing thread to run destructor on, create a new thread.
+        // Do not run destructor on current (event loop) thread since
+        // destructors could be making IPC calls or doing expensive
+        // cleanup.
+        if (m_owned) {
+            m_connection->addCleanup(MakeAsyncCallable(
+                kj::mvCapture(std::thread{[impl]() { delete impl; }}, [](std::thread&& thread) { thread.join(); })));
+        }
+        m_impl = nullptr;
+        m_owned = false;
+    }
+    RemoveClient(m_connection->m_loop);
+}
