+template <typename Value>
+struct RefEmplace
+{
+    RefEmplace(Value& value) : m_value(value) {}
+
+    template <typename... Params>
+    Value& operator()(Params&&... params)
+    {
+        return m_value = Value(std::forward<Params>(params)...);
+    }
+    Value& m_value;
+};
