+static RPCHelpMan setdescriptorhdkey()
+{
+    return RPCHelpMan{"setdescriptorhdkey",
+        "Set or generate a new HD key for this descriptor wallet. The key will only be used for new automatically generated descriptors created with 'createwalletdescriptor'."
+        "No new descriptors will be created by using this RPC. It only prepares the wallet to do so."
+        "\nNote that you will need to MAKE A NEW BACKUP of your wallet after setting the HD wallet key." +
+        HELP_REQUIRING_PASSPHRASE,
+        {{"key", RPCArg::Type::STR, RPCArg::DefaultHint{"random seed"}, "A BIP 32 extended private key (xprv) to set as the wallet's HD Key, or a BIP 32 HD seed encoded as a WIF private key or hex string."}},
+        RPCResult{RPCResult::Type::NONE, "", ""},
+        RPCExamples{
+            HelpExampleCli("setdescriptorhdkey", "")
+            + HelpExampleRpc("setdescriptorhdkey", "")
+        },
+    [&](const RPCHelpMan& self, const JSONRPCRequest& request) -> UniValue
+{
+    std::shared_ptr<CWallet> const pwallet = GetWalletForJSONRPCRequest(request);
+    if (!pwallet) return UniValue::VNULL;
+
+    if (pwallet->IsWalletFlagSet(WALLET_FLAG_DISABLE_PRIVATE_KEYS)) {
+        throw JSONRPCError(RPC_WALLET_ERROR, "Cannot set a HD key to a wallet with private keys disabled");
+    }
+
+    //  Make sure wallet is a descriptor wallet
+    if (!pwallet->IsWalletFlagSet(WALLET_FLAG_DESCRIPTORS)) {
+        throw JSONRPCError(RPC_WALLET_ERROR, "setdescriptorhdkey is not available for non-descriptor wallets");
+    }
+    EnsureWalletIsUnlocked(*pwallet);
+
+    KeyManager& keyman = pwallet->GetKeyManager();
+    if (request.params[0].isNull()) {
+        // Generate new key if none is specified
+        keyman.GenerateAndSetHDKey();
+    } else {
+        // First try decoding as xprv
+        std::string key_str = request.params[0].get_str();
+        CExtKey master_key = DecodeExtKey(key_str);
+        if (!master_key.key.IsValid()) {
+            // The following are for handling the key as a seed
+            std::vector<unsigned char> seed_bytes;
+            // Next try decoding as WIF
+            CKey key = DecodeSecret(key_str);
+            seed_bytes.assign(key.begin(), key.end());
+            if (!key.IsValid()) {
+                // Lastly try decoding hex
+                if (!IsHex(key_str)) {
+                    throw JSONRPCError(RPC_INVALID_ADDRESS_OR_KEY, "Key must be a BIP 32 extended private key, or the BIP 32 seed as a WIF private key or a hex string.");
+                }
+                seed_bytes = ParseHex(key_str);
+            }
+            master_key.SetSeed(MakeByteSpan(seed_bytes));
+        }
+        CExtPubKey master_xpub = master_key.Neuter();
+
+        // Write the key
+        WalletBatch batch(pwallet->GetDatabase());
+        bool ok = keyman.AddHDKey(batch, master_key, master_xpub);
+        assert(ok);
+        keyman.SetActiveHDKey(master_xpub);
+    }
+
+    return UniValue::VNULL;
+}
+    };
+}
