             }
             boundSockets.push_back(bind_handle);
         } else {
-            LogPrintf("Binding RPC on address %s port %i failed.\n", i->first, i->second);
+            LogPrintf("Binding RPC on address %s port %i failed.\n", raw_addr, port);
         }
     }
     return !boundSockets.empty();

