         const uint256& ancestor_hash,
         const FoundBlock& ancestor_out={}) = 0;

+    //! Get a CBlock by its index and return whether successful.
+    virtual bool getBlockByIndex(CBlock& block, const CBlockIndex* index) = 0;

@@ -1431,7 +1431,9 @@ bool AppInitMain(NodeContext& node, interfaces::BlockAndHeaderTipInfo* tip_info)
     };
     Assert(!ApplyArgsManOptions(args, chainman_opts)); // no error can happen, already checked in AppInitParameterInteraction

-    BlockManager::Options blockman_opts{};
+    BlockManager::Options blockman_opts{
+        .blocks_dir = args.GetBlocksDirPath(),
+    };

