@@ -37,6 +37,19 @@ void init_block_index()
     SelectParams(ChainType::MAIN);
 }
 
+CBlockFileInfo CreateCBlockFileInfo(FuzzedDataProvider& fuzzed_data_provider)
+{
+    CBlockFileInfo block_file_info;
+    block_file_info.nBlocks = fuzzed_data_provider.ConsumeIntegral<unsigned int>();
+    block_file_info.nSize = fuzzed_data_provider.ConsumeIntegral<unsigned int>();
+    block_file_info.nUndoSize = fuzzed_data_provider.ConsumeIntegral<unsigned int>();
+    block_file_info.nHeightFirst = fuzzed_data_provider.ConsumeIntegral<unsigned int>();
+    block_file_info.nHeightLast = fuzzed_data_provider.ConsumeIntegral<unsigned int>();
+    block_file_info.nTimeFirst = fuzzed_data_provider.ConsumeIntegral<uint64_t>();
+    block_file_info.nTimeLast = fuzzed_data_provider.ConsumeIntegral<uint64_t>();
+    return block_file_info;
+}
