+    {
+        bool started = m_tp->Start(Sv2TemplateProviderOptions { .port = 18447 });
+        if (! started) return false;
+        // Avoid "Connection refused" on CI:
+        UninterruptibleSleep(std::chrono::milliseconds{1000});
+        return true;
+    }
