+        node::Sv2NetHeader setup_conn_header = node::Sv2NetHeader{node::Sv2MsgType::SETUP_CONNECTION, static_cast<uint32_t>(bytes.size())};
+        return node::Sv2NetMsg{std::move(setup_conn_header), std::move(bytes)};
+    }
