+        TRANSACTIONS.emplace_back(tx_parent);
+        TRANSACTIONS.emplace_back(MakeTransactionSpending({COutPoint{tx_parent->GetHash(), 0}},
+                                                            /*num_outputs=*/1, /*add_witness=*/true));
