+    return mutated_tx;
+}
+
+static CTransactionRef StripWitness(const CTransactionRef& ptx)
+{
+    CMutableTransaction tx(*ptx);
+    tx.vin[0].scriptWitness.stack.clear();
+    auto stripped_tx = MakeTransactionRef(tx);
+    assert(ptx->GetHash() == stripped_tx->GetHash());
+    return stripped_tx;
+}
