+{
+    TestPeer peer;
+    peer.m_nodeid = nodeid;
+    peer.m_good = force_good || fuzzed_data_provider.ConsumeBool();
+    peer.m_connection_preferred = preferred;
+    peer.m_relay_permissions = fuzzed_data_provider.ConsumeBool();
