+    PossibleResponse::NOTHING,
+    PossibleResponse::TX_WITNESS_STRIPPED,
+    PossibleResponse::TX_WITNESS_MALLEATED,
+    PossibleResponse::NOTFOUND,
+    PossibleResponse::DEFER,
+};
