+static TxValidationResult TESTED_TX_RESULTS[] = {
+    // Skip TX_RESULT_UNSET
+    TxValidationResult::TX_CONSENSUS,
+    TxValidationResult::TX_RECENT_CONSENSUS_CHANGE,
+    TxValidationResult::TX_INPUTS_NOT_STANDARD,
