Code review ACK 705105de4f84f2ce3bb1d754c88c32349e01bb3b

Nice cleanup. Previous for loop was very confusing and new code is straightforward.

Not important, but I think it would be a little better if `InitAndLoadChainstate` simply returned `ChainstateLoadStatus` instead of `std::tuple</*may_retry*/ bool, /*error*/ bilingual_str>`. This way retry logic would be implemented in one function, not split across two functions. And it would avoid introducing a new way to represent success/interrupted/failure status, and just reuse the status type we already have:

<details><summary>diff</summary>
<p>

```diff
--- a/src/init.cpp
+++ b/src/init.cpp
@@ -123,6 +123,7 @@ using node::ApplyArgsManOptions;
 using node::BlockManager;
 using node::CacheSizes;
 using node::CalculateCacheSizes;
+using node::ChainstateLoadStatus;
 using node::DEFAULT_PERSIST_MEMPOOL;
 using node::DEFAULT_PRINT_MODIFIED_FEE;
 using node::DEFAULT_STOPATHEIGHT;
@@ -1185,23 +1186,24 @@ bool CheckHostPortOptions(const ArgsManager& args) {
 
 // A GUI user may opt to retry once if there is a failure during chainstate initialization.
 // The function therefore has to support re-entry.
-static std::tuple</*may_retry*/ bool, /*error*/ bilingual_str> InitAndLoadChainstate(
+static ChainstateLoadStatus InitAndLoadChainstate(
     NodeContext& node,
     bool do_reindex,
     const bool do_reindex_chainstate,
     CacheSizes& cache_sizes,
-    const ArgsManager& args)
+    const ArgsManager& args,
+    bilingual_str& error)
 {
+    error.clear();
     const CChainParams& chainparams = Params();
     CTxMemPool::Options mempool_opts{
         .check_ratio = chainparams.DefaultConsistencyChecks() ? 1 : 0,
         .signals = node.validation_signals.get(),
     };
     Assert(ApplyArgsManOptions(args, chainparams, mempool_opts)); // no error can happen, already checked in AppInitParameterInteraction
-    bilingual_str mempool_error;
-    node.mempool = std::make_unique<CTxMemPool>(mempool_opts, mempool_error);
-    if (!mempool_error.empty()) {
-        return {/*may_retry*/ false, /*error*/ mempool_error};
+    node.mempool = std::make_unique<CTxMemPool>(mempool_opts, error);
+    if (!error.empty()) {
+        return ChainstateLoadStatus::FAILURE_FATAL;
     }
     LogPrintf("* Using %.1f MiB for in-memory UTXO set (plus up to %.1f MiB of unused mempool space)\n", cache_sizes.coins * (1.0 / 1024 / 1024), mempool_opts.max_size_bytes * (1.0 / 1024 / 1024));
     ChainstateManager::Options chainman_opts{
@@ -1220,7 +1222,8 @@ static std::tuple</*may_retry*/ bool, /*error*/ bilingual_str> InitAndLoadChains
     try {
         node.chainman = std::make_unique<ChainstateManager>(*Assert(node.shutdown), chainman_opts, blockman_opts);
     } catch (std::exception& e) {
-        return {/*may_retry*/ false, /*error*/ strprintf(Untranslated("Failed to initialize ChainstateManager: %s"), e.what())};
+        error = Untranslated(strprintf("Failed to initialize ChainstateManager: %s", e.what()));
+        return ChainstateLoadStatus::FAILURE_FATAL;
     }
     ChainstateManager& chainman = *node.chainman;
     // This is defined and set here instead of inline in validation.h to avoid a hard
@@ -1266,7 +1269,8 @@ static std::tuple</*may_retry*/ bool, /*error*/ bilingual_str> InitAndLoadChains
             return std::make_tuple(node::ChainstateLoadStatus::FAILURE, _("Error opening block database"));
         }
     };
-    auto [status, error] = catch_exceptions([&] { return LoadChainstate(chainman, cache_sizes, options); });
+    ChainstateLoadStatus status;
+    std::tie(status, error) = catch_exceptions([&] { return LoadChainstate(chainman, cache_sizes, options); });
     if (status == node::ChainstateLoadStatus::SUCCESS) {
         uiInterface.InitMessage(_("Verifying blocks…").translated);
         if (chainman.m_blockman.m_have_pruned && options.check_blocks > MIN_BLOCKS_TO_KEEP) {
@@ -1276,13 +1280,9 @@ static std::tuple</*may_retry*/ bool, /*error*/ bilingual_str> InitAndLoadChains
         std::tie(status, error) = catch_exceptions([&] { return VerifyLoadedChainstate(chainman, options); });
         if (status == node::ChainstateLoadStatus::SUCCESS) {
             LogPrintf(" block index %15dms\n", Ticks<std::chrono::milliseconds>(SteadyClock::now() - load_block_index_start_time));
-            return {/*may_retry*/ false, /*error*/ bilingual_str{}};
         }
     }
-    if (status == node::ChainstateLoadStatus::FAILURE_FATAL || status == node::ChainstateLoadStatus::FAILURE_INCOMPATIBLE_DB || status == node::ChainstateLoadStatus::FAILURE_INSUFFICIENT_DBCACHE) {
-        return {/*may_retry*/ false, /*error*/ error};
-    }
-    return {/*may_retry*/ true, /*error*/ error};
+    return status;
 };
 
 bool AppInitMain(NodeContext& node, interfaces::BlockAndHeaderTipInfo* tip_info)
@@ -1638,13 +1638,9 @@ bool AppInitMain(NodeContext& node, interfaces::BlockAndHeaderTipInfo* tip_info)
     const bool do_reindex_chainstate{args.GetBoolArg("-reindex-chainstate", false)};
 
     // Chainstate initialization and loading may be retried once with reindexing by GUI users
-    auto [maybe_retry, error] = InitAndLoadChainstate(
-        node,
-        do_reindex,
-        do_reindex_chainstate,
-        cache_sizes,
-        args);
-    if (maybe_retry && !do_reindex && !ShutdownRequested(node)) {
+    bilingual_str error;
+    ChainstateLoadStatus status = InitAndLoadChainstate(node, do_reindex, do_reindex_chainstate, cache_sizes, args, error);
+    if (status == ChainstateLoadStatus::FAILURE && !do_reindex && !ShutdownRequested(node)) {
         // suggest a reindex
         bool do_retry = uiInterface.ThreadSafeQuestion(
             error + Untranslated(".\n\n") + _("Do you want to rebuild the block database now?"),
@@ -1658,14 +1654,9 @@ bool AppInitMain(NodeContext& node, interfaces::BlockAndHeaderTipInfo* tip_info)
         if (!Assert(node.shutdown)->reset()) {
             LogError("Internal error: failed to reset shutdown signal.\n");
         }
-        std::tie(std::ignore, error) = InitAndLoadChainstate(
-            node,
-            do_reindex,
-            do_reindex_chainstate,
-            cache_sizes,
-            args);
+        status = InitAndLoadChainstate(node, do_reindex, do_reindex_chainstate, cache_sizes, args, error);
     }
-    if (!error.empty()) {
+    if (status != ChainstateLoadStatus::SUCCESS && status != ChainstateLoadStatus::INTERRUPTED) {
         return InitError(error);
     }
 
```
</p>
</details>
